import get from 'lodash/get';
import has from 'lodash/has';
import set from 'lodash/set';
export class SelectionControls {
  constructor(obj) {
    if (obj instanceof SelectionControls) {
      return obj;
    }
    // Add props
    for (const name of Object.keys(obj)) {
      this[name] = obj[name];
    }
  }
  getProp(key) {
    return get(this, key);
  }
  hasProp(key) {
    return has(this, key);
  }
  setProp(key, value) {
    set(this, key, value);
  }
}
